INSERT INTO `t_hospital` VALUES (100770, NULL, NULL, 1001, 1009, '长治医学院附属和平医院', NULL, '历史沿革。长治医学院附属和平医院成立于1946年7月1日，前身是中国人民解放军晋冀鲁豫军区白求恩国际和平医院总院。在党中央、毛泽东主席的亲切关怀下，由刘伯承、 邓小平、薄一波等老一辈革命家亲手创建，是一所具有悠久历史和光荣革命传统的红色医院，也是山西省首批通过评审的综合性三级甲等医院。', '', '86-0355-3128018 3128457', NULL, NULL, 'http://www.hp1946.cn/', NULL, '10100000', '10140000', '10140400', '长治市延安南路110号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"李宏倬\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"宋志斌\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"name\":\"张文平\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"name\":\"张晓莹\",\"title\":\"秘书\"}],\"key1\":\"0355-3128367\",\"key10\":\"可根据项目情况增加会议次数\",\"key17\":\"初始审查费5000元/项，修正案3000元/项\",\"key2\":\"0355-3128367\",\"key3\":\"heping_irb@126.com\",\"key5\":\"山西长治市延安南路110号 和平精准健康管理楼三楼\",\"key8\":\"工作日8:00-12:00, 15:00-18:00\",\"key9\":\"每月最后一周的周四下午\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"尚进平\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"name\":\"连长红\",\"title\":\"副主任\"},{\"cellStyleMap\":{},\"name\":\"程林忠\",\"telePhone\":\"0355-3128121\",\"title\":\"机构办主任\"},{\"cellStyleMap\":{},\"name\":\"秦江波\",\"telePhone\":\"0355-3128121\",\"title\":\"机构办副主任\"}],\"key46\":\"长治市延安南路110号 长治医学院附属和平医院 精准健康管理楼三楼\",\"key47\":\"0355-3128770\",\"key49\":\"0355-3128770\",\"key50\":\"hplcsyjg@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022117096, 10010, 1583919355493, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
