INSERT INTO `t_hospital` VALUES (100772, NULL, NULL, 1001, 1009, '浙江大学医学院附属第二医院', 'The Second Affiliated hospital of Zhejiang University School of Medicine', '  浙江大学医学院附属第二医院（简称“浙大二院”）创建于1869年，曾创办国内最早的西医院校之一——广济医校，是浙江省西医的发源地，也是国内首家三甲医院。 ', '', '86-0571-87783777', NULL, NULL, 'http://www.z2hospital.com/', NULL, '10100000', '10330000', '10330100', '杭州市解放路88号', NULL, '310009', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0571-87783508\",\"key2\":\"0571-87783969\\n\",\"key3\":\"shaorong05405@126.com\",\"key5\":\"杭州市解放路路88号门诊16楼1601室\",\"key8\":\"周一~周五\\n\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key4\":\"周一至周五\",\"key46\":\"杭州市解放路路88号门诊16楼1601室\\n\",\"key47\":\"0571-87783969\\n\",\"key49\":\"0571-87783759\",\"key50\":\"ruanzr@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022117152, 10010, 1583919483653, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100773, NULL, NULL, 1001, 1009, '浙江大学医学院附属第一医院', NULL, '       浙江大学附属第一医院是集医疗、教学、科研于一体的大型三级甲等综合医院，以综合实力雄厚，医疗质量过硬，学科特色鲜明享誉海内外；同时也是浙江省医疗、教学、科研指导中心。建院至今，形成了“以卓越的医疗品质促进人类健康”的使命和“成为国际一流的医学中心”的愿景，创立了“严谨求实”的核心价值观和“以严谨的教育培养为基石，创新的科学研究为引领，卓越的医疗技术为保障，一流的健康服务为目标，依托科学的规范化管理，实现员工自我价值，创建国际一流医学中心”的发展思路。', '', '86-0571-87236668,0571-872361', NULL, NULL, 'http://www.zy91.com/index/index.jhtml', NULL, '10100000', '10330000', '10330100', '杭州市庆春路79号', NULL, '310003', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0571-87236685\",\"key2\":\"0571-87236685\",\"key3\":\"yixuelunli123@163.com\",\"key5\":\"浙江省杭州市老浙大直路17号医院2号楼3楼312室\",\"key8\":\"周四、周五\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key4\":\"周一至周五\",\"key46\":\"浙江省杭州市庆春路79号医院6号楼A5\",\"key47\":\"0571-89992206\",\"key49\":\"\\n0571-87236560；0571-87236537\",\"key50\":\"zyct79@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022117177, 10010, 1583919486923, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100774, NULL, NULL, 1005, 1009, '浙江大学医学院附属儿童医院', 'The Children\'s Hospital Zhejiang University School of Medicine', '浙江大学医学院附属儿童医院(浙江省儿童医院、浙江省儿童保健院)前身是1951年成立的“浙江省立妇幼保健院”，创始人和首任院长为我国著名儿科专家厉矞华教授。2019年5月，医院正式获批国家儿童健康与疾病国家临床医学研究中心。作为儿科学国家重点专科单位，医院承担着全省儿科医疗、科研、教学、儿童保健及突发性公共卫生事件应急任务。医院现设有滨江和湖滨两院区，核定床位1900张，现有职工共2400人，其中正高级职称120人，副高级职称190人。', '', '86-0571-87061007', NULL, NULL, 'http://www.zjuch.cn/', NULL, '10100000', '10330000', '10330100', '杭州市竹竿巷57号 邮编：310003', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"漆林艳\",\"position\":\"伦理秘书\",\"telePhone\":\"zuchiec@163.com\"}],\"key1\":\"0571-86670076，0571-87060205；0571-87060205 ;0571-88873102\",\"key2\":\"0571-86029132，0571-87921114\",\"key3\":\"zuchiec@163.com\",\"key5\":\"浙江大学医学院附属儿童医院 杭州市下城区延安路竹竿巷57号 滨江院区住院部1楼1097室\",\"key8\":\"周一至周五，上午8:00-12:00，下午13:30-17:00\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"zuchgcp@163.com\",\"name\":\"李春梅\",\"position\":\"机构秘书\",\"telePhone\":\"0571-86670076\"}],\"key4\":\"工作日周一、周三至周五（周二例会）上午8:00-12:00 ，下午 13:30-17:00\",\"key46\":\"浙江省杭州滨江院区住院楼1楼1098室\",\"key47\":\"0571-85135220，0571-86029132\",\"key49\":\"0571-86670075，0571-86670076\",\"key50\":\"zuchgcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022117210, 10010, 1583919484690, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100775, NULL, NULL, 1005, 1009, '浙江大学医学院附属妇产科医院', 'WOMEN\'S HOSPITAL SCHOOL OF MEDICINE ZHEJIANG UNIVERSITY', '浙江大学医学院附属妇产科医院（浙江省妇女保健院、浙江省妇女医院）是浙江省妇产科医疗、教学、科研及计划生育、妇女保健工作的指导中心。三级甲等妇产科医院（妇女保健院）。曾先后获得全国妇幼卫生先进集体、全国计划生育先进集体、全国优秀爱婴医院、全国母婴友好医院、浙江省市文明医院、浙江省示范文明医院、浙江省计划生育科研先进集体等荣誉。核定床位1120张。职工1900余人，其中副高级以上职称的专家200余人。', '', '86-0571-87061501', NULL, NULL, 'http://www.womanhospital.cn/', NULL, '10100000', '10330000', '10330100', '杭州市学士路一号', NULL, '310006', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0571-89991836\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"浙江省杭州市学士路1号\",\"key47\":\"0571-89992206\",\"key49\":\"0571-89992206\",\"key50\":\"1835112846@qq.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022117236, 10010, 1583919486991, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100776, NULL, NULL, 1001, 1009, '浙江大学医学院附属邵逸夫医院', NULL, '医院于2006年12月顺利通过了国际医院评审联合委员会的评审，成为中国大陆首家通过国际医院评审（JCI *)的公立医院；2017年通过国际医疗信息化最高级别认证HIMSS7**；2017年成为中国首家加入MAYO CLINIC医疗联盟的公立医院***。2013年1月，医院荣获“全国卫生系统先进集体”荣誉称号****；2014—2018年，医院连续五年荣获“中国医疗机构最佳雇主”荣誉称号*****；2018年荣获“人文爱心医院”荣誉称号******；2019年通过磁性医院认证。', '', '86-0571-8609 0073', NULL, NULL, 'http://www.srrsh.com/', NULL, '10100000', '10330000', '10330100', '浙江杭州庆春东路3号', NULL, '310016', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0571-86006811\",\"key2\":\"0571-86960497\",\"key3\":\"594961420@qq.com\",\"key5\":\"浙江省杭州市江干区庆春东路3号邵逸夫医院\",\"key8\":\"工作日：8:00-11:30；13:30-17:00\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"823366612@qq.com\",\"name\":\"徐叶偲、耿蔚然\",\"position\":\"机构秘书\",\"telePhone\":\"0571-86960497　\"}],\"key4\":\"工作日：8:00-11:30；13:30-17:00\",\"key46\":\"浙江省杭州市江干区庆春东路3号 浙江大学医学院附属邵逸夫医院 4号楼911 临床试验机构办公室\",\"key47\":\"0571-86960497\",\"key49\":\"0571-86006987\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022117262, 10010, 1583919485649, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
