INSERT INTO `t_hospital` VALUES (100778, NULL, NULL, 1003, 1009, '浙江省立同德医院', NULL, '浙江省立同德医院是浙江省卫生健康委员会直属的三级甲等中西医结合医院，是全国重点中西医结合医院、全国重点中医药研究院建设单位，是美国南加州凯克医学中心合作医院、意大利锡耶纳大学附属医院和罗马纳里医院友好医院、韩国东新大学和韩国庆熙大学友好单位、台北医学大学附属医院姐妹医院、香港大学中医药学院合作院校。', '', '86-0571-89972114', NULL, NULL, 'http://www.zjtongde.com/', NULL, '10100000', '10330000', '10330100', '杭州市古翠路234号 ', 'No. 234 Gucui Road, Hangzhou', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"liyapin0186@vip.sina.com\",\"name\":\"李亚平\",\"position\":\"伦理委员兼伦理秘书\"}],\"key1\":\"0571-89972025\",\"key3\":\"liyapin0186@vip.sina.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"浙江省杭州市古翠路234号\",\"key47\":\"0571-89972066\",\"key49\":\"0571-89972026，0571-89972066\",\"key50\":\"tdkjk2011@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022117313, 10010, 1583919486923, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100779, NULL, NULL, 1001, 1009, '浙江省台州医院', 'Taizhou Enze Medical Center', '\n　　浙江省台州医院位于国家历史文化名城——台州临海，是一家集医疗、科研、教学、预防为一体的三级甲等综合性医院，其前身是1901年英国传教士创建的恩泽医局，现为台州恩泽医疗中心（集团）总部所在地和龙头医院。是国家住院医师规范化培训基地，国家级博士后科研工作站、浙江省院士工作站、挂名“浙江大学台州医院”，是浙江大学附属医院、温州医科大学附属医院，是浙江大学医学院、南京大学医学院、杭州医学院的教学医院，美国芝加哥医学院及瑞典卡罗琳斯卡大学医学院友好医院，上海交通大学医学院附属新华医院联盟医院。', '', '4001576120', NULL, NULL, 'http://www.tzhospital.com/', NULL, '10100000', '10330000', '10331000', '浙江省临海市西门街150号', NULL, '317000', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0576-85199816\",\"key3\":\"wanggg@enzemed.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"浙江省临海市西门街150号\",\"key49\":\"0576-85199810\",\"key50\":\"tzyyGCP@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022117340, 10010, 1583919484722, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
