INSERT INTO `t_hospital` VALUES (100781, NULL, NULL, 1005, 1009, '浙江省肿瘤医院', 'Zhejiang Cancer Hospital﻿', '浙江省肿瘤医院始建于1963年10月，是新中国成立最早的四所肿瘤医院之一,集肿瘤预防、医疗、科研、教学、康复于一体，承担着国家肿瘤防治重任，在全国的肿瘤防治工作中发挥着龙头作用，连续8年名列中国最佳肿瘤专科排行前十。2019年5月，浙江省人民政府与中国科学院正式签署医学合作协议，浙江省肿瘤医院正式挂牌“中国科学院肿瘤与基础医学研究所、中国科学院大学附属肿瘤医院、中国科学院大学杭州临床医学院”。', '', '86-0571 - 88122222', NULL, NULL, 'http://www.zchospital.com/index.php/Index/indexa', NULL, '10100000', '10330000', '10330100', '杭州市拱墅区半山东路1号', 'No 1, East Banshan Road, Gongshu District,\n\n\n                 Hangzhou , P.R. Chi', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0571-88122146\",\"key3\":\"zjzliec@163.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"hongdan@zjcc.org.cn\",\"name\":\"洪丹\",\"telePhone\":\"0571-88122261\"},{\"cellStyleMap\":{},\"email\":\"lify1697@zjcc.org.cn\",\"name\":\"李飞燕\",\"telePhone\":\"0571-88122482\"}],\"key46\":\"浙江省杭州市拱墅区半山东路一号 浙江省肿瘤医院15号楼204-207室\",\"key47\":\"0571-88122167\",\"key49\":\"0571-88122167\",\"key50\":\"zjchgcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022117394, 10010, 1583919487312, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
