INSERT INTO `t_hospital` VALUES (100790, NULL, NULL, 1001, 1009, '郑州大学第一附属医院', 'The First Affiliated Hosptial of Zhengzhou University', 'http://fcc.zzu.edu.cn/s_lan1.htm', '', '0371-66913114', '0371-66970906', 'Yuanban3031@163.com', 'http://fcc.zzu.edu.cn/default.htm', '郑州大学第一附属医院', '10100000', '10410000', '10410100', '河南省郑州市建设东路1号', '', '450052', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"刘章锁\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"陈清江\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"email\":\"zhaojingyxj@126.com\",\"mobilePhone\":\" 0371-66295219\",\"name\":\"赵菁\"}],\"key1\":\" 0371-66295219\",\"key10\":\"每月1次\",\"key13\":\"户名：郑州大学第一附属医院，开户银行：工商银行商都路支行，帐号：1702 0208 0900 8900 162，财务处电话：0371-66913066。公对公转账，或者私对公转账均可。\\n\\nb. 凭打款凭证在公共邮箱收件箱（zdyfylunli@126.com，密码zdyfylunli1），找到“伦理审查申请单”，PI签字后递交田老师盖伦理办公室章。\\n\\nc. 拿签字、盖章的“伦理审查申请单”到财务处开发票，发票中收款项目内容填写:“PI+伦理费”，如：张明智伦理费。我院财务处开据的发票为“河南省行政事业单位资金往来结算票据”，不含税，没有税。\\n\\n\\n\\n4. 伦理纸质版资料准备和模板按照公共邮箱要求，2套原件（机构和伦理各存档一套），复印件13套（有13个委员审查资料）。\\n\\n备注：可以先打印出1套资料，不必胶装、不必签字盖章，由田老师初审（伦理受理时间：周一到周五上午，特殊情况另行通知）。定稿版本签字盖章后到伦理档案室归档1份原件并登记。电子版资料需要按照要求发送给伦理老师邮箱tianli@163.com\\n\\n注意：PI务必到场（汇报时间控制在5min），若不参会需办理正规请假手续。PI在场的情况下，研究团队成员可以协助PI汇报和答辩。有申办方和CRO公司的项目，申办方或者CRO必须有一方出席会议。\\n\\n \\n\\n5. 伦理会后，请项目申请者将13份复印件回收，资料信息较多，以免泄漏信息。\\n\\n6. 伦理审查（初始审查、初审后复审、修正案审查、SAE、方案违背、年度跟踪、结题审查）所需资料准备以及详细的注意事项，均在该邮箱的收件箱，禁止私自修改密码，请自觉遵守，违者必究。\",\"key15\":\"每月15日前递交资料，可以参加下月伦理会议\",\"key16\":\"是\",\"key17\":\"5000\",\"key18\":\"15天左右\",\"key19\":\"否\",\"key2\":\" 0371-66295219\",\"key20\":\"是\",\"key21\":\"3500\",\"key26\":\"是\",\"key27\":\"否\",\"key28\":\"否\",\"key3\":\"zdyfylunli@126.com\",\"key30\":\"否\",\"key5\":\"郑州市建设东路1号郑州大学第一附属医院9号楼105房间\",\"key6\":\"450052\",\"key8\":\"周一至周五全天，周六上午，上会周不接待\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"否\",\"key2\":\"否\"},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"ZDYFYgcp@164.com\",\"name\":\"张晓坚\",\"title\":\"机构办公室主任\"},{\"cellStyleMap\":{},\"name\":\"秦贵军\",\"title\":\"机构副主任\"},{\"cellStyleMap\":{},\"name\":\"席晨\",\"title\":\"机构质控\"},{\"cellStyleMap\":{},\"name\":\"卫丹\",\"title\":\"机构药物管理员\"},{\"cellStyleMap\":{},\"mobilePhone\":\"13721417344\",\"name\":\"王肖雲\",\"telePhone\":\"0371-166295625\",\"tencentNum\":\"cloudnine5197\",\"title\":\"机构秘书\"},{\"cellStyleMap\":{},\"name\":\"薛文华\",\"tencentNum\":\"xwh509\",\"title\":\"机构秘书\"}],\"key1\":\"http://fcc.zzu.edu.cn/yaowu/\",\"key10\":\"是\",\"key11\":\"否\",\"key13\":\"不需要立项会议，直接机构秘书审核后无问题签署受理通知单\",\"key18\":\"否\",\"key19\":\"否\",\"key20\":\"否\",\"key21\":\"否\",\"key22\":\"否\",\"key23\":\"纸质版\",\"key24\":\"否\",\"key25\":\"纸质版\",\"key31\":\"是\",\"key32\":\"在机构席晨老师处需要登记后排队预约\",\"key33\":\"参见共邮\",\"key34\":\"参见共邮，需要自备文件盒\",\"key35\":\"是\",\"key38\":\"是\",\"key39\":\"随时和邵欣老师联系\",\"key4\":\"周一、周三、周四上午不接待\",\"key40\":\"根据资料准备情况\",\"key41\":\"周一、周三、周四上午不接待\",\"key42\":\"参看机构共邮\",\"key43\":\"是\",\"key44\":\"观察费均值*0.2*受试者例数*访视次数\",\"key45\":\"4份，需要骑缝章。\",\"key46\":\"河南省郑州市建设东路1号\",\"key48\":\"450052\",\"key49\":\"0371-6691309\",\"key50\":\"ZDYFYgcp@163.com\",\"key6\":\"是\",\"key8\":\"可以\",\"key9\":\"三级甲等\"},\"pharmacyData\":{\"cellStyleMap\":{},\"key1\":\"是\"},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"key1\":\"是\",\"key10\":\"机构备案（CV GCP证书、毕业证书），签署CRC（医院自己的模板）\",\"key2\":\"三方协议\",\"key3\":\"否\",\"key4\":\"否\",\"key5\":\"必须用医院自己模板，模板参见机构共邮\",\"key8\":\"是\",\"smoAndCroList\":[]}}', NULL, '刘章锁', 1, 1564022117634, 10010, 1583919153694, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
