INSERT INTO `t_hospital` VALUES (100797, NULL, NULL, 1001, 1009, '中国科学院大学宁波华美医院', 'Hwa Mei Hospial,University of Chinese Academy of Sciences(Ningbo No.2 Hospital)', '中国科学院大学宁波华美医院（宁波市第二医院），1843年由美国基督浸礼会传教士马高温（D.J.Macgowan）兴办，迄今有175年历史，是中国最早建立的西医医院之一。医院坐落于东海之滨、长江三角洲南翼经济中心宁 波，地处三江文化长廊源头的姚江河畔，与护城河北斗河相邻，临江伴水、环境优雅宁静，是浙东地区具有重要影响力的集医疗、教学、科研、预防、保健于一体的现代化三级甲等综合性医院。医院是中国科学院大学的附属医院，还是温州医学院、浙江中医药大学、皖南医学院等医学院校教学医院，是首批国家级住院医师规范化培训基地，国际SOS紧急救援中心网络医院。', '', '86-0574-87362216//87366901(总)', NULL, NULL, 'http://www.nbdeyy.com/', NULL, '10100000', '10330000', '10330200', '宁波市西北街41号', '', '315010', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"rypamy@163.com\",\"mobilePhone\":\"13456152203\",\"name\":\"任燕萍\"}],\"key1\":\"0574-83870361\",\"key3\":\"rypamy@163.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"nbhuxiaohong@sina.com\",\"name\":\"胡晓红\"}],\"key46\":\"浙江省宁波市西北街41号宁波市第二医院3号楼-4号楼间药物临床试验机构办公室\",\"key47\":\"0574-83870375\",\"key49\":\"0574-83870375\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022117825, 10010, 1583919486298, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
