INSERT INTO `t_hospital` VALUES (100838, NULL, NULL, 1001, 1009, '中南大学湘雅三医院', 'The Third Xiangya Hospital of Central South University', '中南大学湘雅三医院（原湖南医科大学附属第三医院）建于1989年，座落在中国历史文化名城长沙，湘江西岸，是国家教育部直属全国重点大学——中南大学附属的大型综合性三级甲等医院，也是国家卫生健康委员会预算管理医院。\n\n医院秉承百年“湘雅”精神积淀，以国内一流大学为学科依托，牢牢坚持以人民健康为中心的发展思想，着力打造“以医疗服务为主体，人才培养、科学研究为侧翼，现代医院科学管理为尾翼”的“一体三翼”发展新格局。经过30年的高速发展，医院综合实力大幅提升，成为了一所集医疗、教学、科研、预防、保健、康复于一体，提供高质量医疗服务、培养高层次医学人才、进行高水平科学研究的现代综合医院。', '', '0731-88618120', NULL, NULL, 'http://www.xy3yy.com/', NULL, '10100000', '10430000', '10430100', '湖南省长沙市岳麓区桐梓坡路', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"卢捷湘\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"王晓敏\",\"title\":\"伦理会秘书\"}],\"key1\":\"0731-88618938\",\"key10\":\"每月1次\",\"key17\":\"会议审查II-IV期5000元，I期4000元\",\"key21\":\"快审1000元\",\"key3\":\"xy3irb@163.com\",\"key5\":\"湖南省长沙市岳麓区筒子坡路138号美容中心4楼\",\"key8\":\"周二和周四下午\",\"key9\":\"每月中下旬\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key4\":\"每周一下午、周二周三周四全天（上午：8:00-12:00，下午：14:30-17:30）\",\"key46\":\"湖南省长沙市岳麓区筒子坡路138号科教楼7楼（701-712）\",\"key47\":\"0731-88618931\",\"key49\":\"0731-88618931\",\"key50\":\"xy3gcpqa@163.com，xy3gcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 1, 1564022118958, 10010, 1583919228351, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (100839, NULL, NULL, 1001, 1009, '中南大学湘雅医院', 'XIANGYA HOSPITAL CENTRAL SOUTH UNIVERSITY', '湘雅遵循“公勇勤慎、诚爱谦廉、求真求确、必邃必专”的湘雅校训，秉承“严谨、团结、求实、进取”的湘雅精神，培养造就了张孝骞、汤飞凡、李振翩等一大批在国内外有影响的医学名医名家。在医学界有“内科全才”之称的张孝骞，系湘雅第一届毕业生，是我国著名内科学家、医学教育家、中国消化病学的奠基人；被称为世界“衣原体之父”的著名微生物学家、免疫学家汤飞凡，在世界第一个成功分离沙眼病毒，获得诺贝尔奖提名。毛泽东同志曾高度赞扬湘雅的医疗水平，并多次介绍亲友来院看病。', '', '0731-89753999', NULL, NULL, 'xiangya.com.cn', '湘雅医院病友服务医务社工中心', '10100000', '10430000', '10430100', '长沙市开福区湘雅路87号', '', NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"\\t张欣\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"email\":\" xyyyllwyh@126.com\",\"mobilePhone\":\"15274870890\",\"name\":\"\\t肖佩君\",\"title\":\"伦理会秘书\"}],\"key1\":\"\\t0731-84327919\",\"key10\":\"1次/1-2月 召开时间\",\"key17\":\"初审6000元\",\"key2\":\"\\t0731-84327919\",\"key3\":\"\\tXYYYLLWYH@126.com\",\"key5\":\"湖南省长沙市湘雅路87号医院药学楼2楼205室\",\"key7\":\"http://www.xiangya.com.cn/web/Content.aspx?chn=2156&id=302\",\"key8\":\"周一周三全天，周二周五上午\",\"key9\":\"每月的中下旬\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"刘昭前\",\"title\":\"机构负责人/机构办主任\"},{\"cellStyleMap\":{},\"name\":\"徐平声 \",\"telePhone\":\"0731-84327458\",\"title\":\"机构副主任/机构办主任\"},{\"cellStyleMap\":{},\"name\":\"秦群\",\"telePhone\":\"0731-84327918\",\"title\":\"办公室副主任\"},{\"cellStyleMap\":{},\"name\":\"周淦 \",\"telePhone\":\"0731-89753403\",\"title\":\"办公室副主任\"},{\"cellStyleMap\":{},\"name\":\"李丹\",\"telePhone\":\"0731-8432791 \",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"name\":\"谭蓉蓉\",\"telePhone\":\"0731-8432791 \",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"name\":\"张苗苗\",\"telePhone\":\"0731-8432791 \",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"name\":\"潘静\",\"telePhone\":\"0731-89753403\",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"name\":\"李岱\",\"telePhone\":\"0731-89753403\",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"name\":\"王成\",\"telePhone\":\"0731-89753403\"