INSERT INTO `t_hospital` VALUES (100878, NULL, NULL, 1001, 1009, '淄博市中心医院', 'ZiBo Central Hospital', '淄博市中心医院始建于1950年，是全市规模最大的三级甲等综合医院，是全市医疗、教学、科研、预防保健、康复中心，是滨州医学院附属医院。\n\n医院目前编制床位1980张（开放床位2100张），职工3300名，其中高级专业技术人员370名；博士72名、硕士740名。2018年完成门诊诊疗150万人次；出院人数8.9万人次；住院病人手术4万余例。医疗设备总值9.34亿元。\n\n医院设临床科室57个，医技科室25个，其中省级临床重点专科6个、全省医药卫生重点专业4个、全省首批精品特色专科1个。陈义汉、李春岩、孙颖浩、董家鸿4名院士先后在我院建立院士工作站。在香港艾力彼医院管理研究中心中国医院竞争力排行榜中，我院跻身全国地市级医院百强。于2018年分别通过了中国医院竞争力五星级认证和三甲医院复审。', '', '0533-2360228', NULL, NULL, 'http://www.zbzxyy.com/', 'zbszxyy', '10100000', '10370000', '10370300', '山东省淄博市张店区共青团西路54号', '54#,Gongqingtuan West Road, Zhangdian District, Zibo City, Shandong Province, Chi', '255020', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0533-2360337\",\"key3\":\"IEC0337@163.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key49\":\"0533-2361126\",\"key50\":\"gcpzbszxyy@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '12370300493224722D', '肖洪涛', 1, 1564022120091, 10010, 1583919343431, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
