INSERT INTO `t_hospital` VALUES (100893, NULL, NULL, 1001, 1009, ' 汕头大学医学院第一附属医院', 'The first affiliated hospital of shantou university medical college', '汕头大学医学院第一附属医院于1989年由李嘉诚基金会捐资兴建，是一所省属公立大型综合性三级甲等医院，先后荣获“全国百佳医院”、“全国百姓放心示范医院”、“全国改革创新医院”、“全国医药卫生系统先进集体”、“全国救灾防病先进集体”、“全国青年文明号”、“广东省抗击非典先进单位”等称号。2014年成为荣获中国医疗机构公信力示范单位的三甲医院，在2013年医院群众满意度第三方评价中名列省属医院第二位。在2018年“中国地级城市医院·竞争力100强”排行榜中列第6位，2018年成为广东省高水平医院建设“登峰计划”首批9家重点建设医院之一。', NULL, NULL, NULL, NULL, NULL, NULL, '10100000', '10440000', '10440500', NULL, NULL, NULL, 0, 0, NULL, NULL, NULL, 10032, 1580886422610, 10032, 1580886422610, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
