INSERT INTO `t_hospital` VALUES (100936, NULL, NULL, 1001, 1009, '中国人民解放军第三0三医院', NULL, '国人民解放军第303医院位于美丽的邕江河畔，从1945年建院至今，走过了60多年的光辉历程，如今她已发展成为一所集医疗、科研、教学和康复保健为一体的现代化综合性三级甲等医院，是第三军医大学、南方医科大学（原第一军医大学）、广西医科大学等高校的教学医院，是自治区、南宁市城镇职工基本医疗保险定点医院，是南宁市首批“百姓放心消费医院”和“绿色环保医院”。医院医疗设备配套齐全，拥有专业技术人员800多名，具有高级职称的专家100多名，硕士、博士等高学历人才60多名。', NULL, '7712870303', '7712870304', NULL, 'http://www.303yy.com', NULL, '10100000', '10110000', '10110000', '广西壮族自治区南宁市', NULL, '530021', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"lunli303@163.com\",\"name\":\"曲海燕\",\"telePhone\":\"0771-2870121\",\"title\":\"主任\"},{\"cellStyleMap\":{},\"email\":\"lunli303@163.com\",\"name\":\"韦坤宇\",\"telePhone\":\"0771-2870121\",\"title\":\"秘书\"}],\"key1\":\"0771-2870121\",\"key3\":\"lunli303@163.com\",\"key5\":\"所在地址:广西南宁市植物路52号解放军三〇三医院科技楼7楼伦理办公室 邮编：530021\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"gcp303@163.com\",\"name\":\"刘长续\",\"telePhone\":\"0771-4978242\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"email\":\"gcp303@164.com\",\"name\":\"叶常青\",\"telePhone\":\"0771-4978242\",\"title\":\"机构副主任\"},{\"cellStyleMap\":{},\"email\":\"gcp303@165.com\",\"name\":\"夏爱军\",\"telePhone\":\"0771-4978242\",\"title\":\"机构办主任\"},{\"cellStyleMap\":{},\"email\":\"gcp303@166.com\",\"name\":\"梁园\",\"telePhone\":\"0771-4978242\",\"title\":\"机构办副主任\"},{\"cellStyleMap\":{},\"email\":\"gcp303@167.com\",\"name\":\"黎雪莲\",\"telePhone\":\"0771-4978242\",\"title\":\"机构秘书\"},{\"cellStyleMap\":{},\"email\":\"gcp303@168.com\",\"name\":\"韦 玲\",\"telePhone\":\"0771-4978242\",\"title\":\"机构秘书\"}],\"key46\":\"所在地址:广西南宁市植物路52号中国人民解放军第三0三医院科技综合楼2楼 邮编：530021\",\"key47\":\"0771-4978242\",\"key49\":\"0771-4978242\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583918982526, 10010, 1584673814259, 0, '', NULL, NULL, NULL, NUL