INSERT INTO `t_hospital` VALUES (100964, NULL, NULL, 1005, 1009, '广州医科大学附属口腔医院', NULL, '广医口腔以口腔专科为主体，集医疗、科研、教学、预防、保健为一体的广州医科大学附属口腔直属医院，是广州口腔医学系的主要教学基地，是广州市基本医疗保险定点医院、广铁集团公司职工基本医疗保险定点医院及广州市新型农村合作医疗市级定点医疗机构。全院采用先进的计算机网络系统进行医疗、教学及行政管理。 　　医院功能齐全、布局合理、环境优雅、温馨，充分体现人文关怀，拥有一批德国、日本、美国等国家先进的口腔综合治疗仪以及各种先进的口腔专科诊断医疗设备，能够满足全部口腔诊疗工作的需要。 　　医院设有口腔内科、口腔颌面外科、口腔修复科、口腔正畸科、口腔种植中心、口腔预防科、洁牙中心、口腔特诊室等临床科室；建立了一支拥有博士后、博士、硕士研究生的高尖端口腔学科人才梯队；能运用国内外先进医疗技术和方法开展上颌窦提升术、全口种植术、冷光美白、颌面部感染和外伤、颌面部畸形及颌面部肿瘤等较高难度手术，是在国内较早开展鼾症手术矫治及口腔矫治器治疗的单位之一。 　　医院消毒措施严格、管理规范，全面实行国际标准的四手化操作，采用\"一人一机\"和使用一次性口腔包，彻底杜绝口腔诊治过程中可能出现的交叉感染，保证患者安全、舒适、放心。 　　医院承担着广州医学院口腔医学系本科生的临床教学任务；注重科研课题研究，获得多项省、市科委、卫生厅(局)的各级科研立项，其中牙周和根管的激光治疗研究已达国内领先水平。', NULL, '020-61350511 ', NULL, NULL, 'www.gykqyy.com', NULL, '10100000', '10440000', '10440100', '广州市荔湾区黄沙大道31、39、59号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"18144896378\",\"name\":\"张清彬\",\"title\":\"机构主任\"}]},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '81440100671801992C', '张曼莉', 10010, 1583919059829, 10010, 1584673844991, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
