INSERT INTO `t_hospital` VALUES (100966, NULL, NULL, 1001, 1009, '中国人民解放军南部战区总医院', NULL, '广州军区广州总医院始建于1933年，其前址为广东省陆、海、空三军联勤总医院，1949年我军接管纳编，1955年命名为广州军区总医院，1976年以流花桥医院注册于世界卫生组织。 医院地处广州市流花路，座落在风景秀丽的流花湖畔，毗邻广交会、中国大酒店和东方宾馆，临近广州火车站、省汽车总站和地铁站，交通方便，地理位置优越，是广州商贸、金融、交通和旅游的中心。医院占地面积约21万平方米，建筑面积24万平方米，设有50余个专业科室，展开病床1200多张，拥有卫生专业技术人员1600余人。年门诊51万余人次，收容1.8万人次，手术6500多台次。“九五”期间医院建设飞速发展，已成为一所规模宏大、专科齐全、人才济济、技术精良、设备先进、环境优雅、服务**，集医疗、教学、科研、预防、保健、康复为一体的现代化大型综合性三级甲等医院，是广东省、广州市首批医疗保险定点医院。医院始终坚持以病人为中心，奉行“服务军民，奉献社会”的宗旨，以高超的技术促进发展，以科学的管理开拓创新，以优质的服务赢得声誉。全院人员求真务实、医德高尚，竭诚为广大患者提供**的医疗保健服务。', NULL, '020-88653111 020-29055511', NULL, NULL, NULL, NULL, '10100000', '10440000', '10440100', '广东省广州市流花路111号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"dcxiang@foxmail.com\",\"name\":\"向定成\",\"position\":\"伦理主任\",\"telePhone\":\"020-88653325\"},{\"cellStyleMap\":{},\"email\":\"gzgcp2012@163.com\",\"name\":\"关慧\",\"position\":\"伦理秘书\",\"telePhone\":\"020-88653435\"}],\"key1\":\"020-88653435\",\"key10\":\"每月一次\",\"key17\":\"7000\",\"key2\":\"020-88653436\",\"key3\":\"gzgcp2012@163.com\",\"key5\":\"广东省广州市流花路111号药学部5楼\",\"key8\":\"周一到周五\",\"key9\":\"每个月最后一周\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"dcxiang@foxmail.com\",\"name\":\"向定成\",\"position\":\"机构主任\",\"telePhone\":\"020-88653325\"},{\"cellStyleMap\":{},\"email\":\"gzgcp2012@163.com\",\"name\":\"关慧\",\"position\":\"机构秘书\",\"telePhone\":\"020-88653435\"}],\"key4\":\"周一到周五，8-11:50,14:30-17:10\",\"key46\":\"广东省广州市流花路111号药学部5楼\",\"key49\":\"020-88653436\\\\88653435\",\"key50\":\"gzgcp2012@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919061056, 10010, 1583919061056, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
