INSERT INTO `t_hospital` VALUES (100969, NULL, NULL, 1005, 1021, '广州复大医疗有限公司复大肿瘤医院', NULL, '广州复大医疗有限公司复大肿瘤医院，是经国家卫生行政部门批准集医疗、教学、科研、预防、康复为一体的专科医院，位于广州市中心的天河区棠德西路二号。设有床位400张，拥有45间高端VIP病房。 自办院以来，始终坚持“厚德行医、医德共济”的院训，坚持“科技兴院、诚信立院、博爱办院”的办院方针，坚持走“科技化、学术化、国际化”道路，努力创建具有特色的现代化医院。广州复大医疗有限公司复大肿瘤医院的管理团队创建包括基本服务、预期服务和惊喜服务在内的BEU服务模式，细致的服务贯穿于患者入院前到出院后，让患者感受到浓浓的人文关怀。广州复大医疗有限公司复大肿瘤医院的医生团队严格执行“三不”制度：不接受红包、不接受回扣、不接受请吃。广州复大医疗有限公司复大肿瘤医院是广州市医保定点医院，并按照要求严格执行收费标准，杜绝随意抬高药品价格、乱收费、滥用药物等现象发生，平价不平质，全面保障患者权益。', NULL, '400-6680120', NULL, NULL, 'http://www.fuda120.com/', NULL, '10100000', '10440000', '10440100', '广州市天河区棠德西路2号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"15915873376\",\"name\":\"何丽华\",\"position\":\"机构秘书\"}]},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', '914401017520773000', '左建生', 10010, 1583919061263, 10010, 1583919061263, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
