INSERT INTO `t_hospital` VALUES (101080, NULL, NULL, 1001, 1009, '华中科技大学同济医学院附属协和医院（肿瘤中心）', NULL, '华中科技大学同济医学院附属协和医院肿瘤中心是华中地区综合医院规模最大、学术力量雄厚的肿瘤学临床、科研、教学基地。肿瘤科从1987年开始组建，1989年开设肿瘤病房，2002年按肿瘤专科医院规模易址扩建，2005年经湖北省卫生厅批准正式更名为“华中科技大学同济医学院附属协和医院肿瘤中心”，2008年被评为湖北省重点学科，2013年被卫计委评为国家临床重点专科。2014年，被评为国家“肿瘤放射治疗科”和“肿瘤内科”住院医师规范化培训基地。肿瘤中心拥有教授/主任医师19人，副教授25人，副主任技师2人，副主任护师2人。现任中心主任和肿瘤学教研室主任为伍钢教授。', NULL, '027-65650733（肿瘤中心）', NULL, NULL, 'http://www.whuh.com/channel/zlzx.html（肿瘤中心）', NULL, '10100000', '10420000', '10420100', '武汉市江汉区邬家墩156号（肿瘤中心）', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[]},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919190489, 10010, 1583919190489, 0, '', 100253, '华中科技大学同济医学院附属协和医院', NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101081, NULL, NULL, 1001, 1013, '武汉市普仁江岸医院', NULL, '武汉市普仁江岸医院地处武汉长江主轴核心区——江岸滨江商务区，创建于1985年，原为中国南车集团武汉江岸车辆厂职工医院，2006年成功改制，成为武汉市普仁医院江岸院区，是国家二级非营利性综合性医院，为武汉市医保定点、工伤保险定点、生育保险定点的医疗机构。', NULL, NULL, NULL, NULL, 'http://www.purenyy.com/html/Branch/ja/2017/0329/2707.html', NULL, '10100000', '10420000', '10420100', '武汉市江岸区徐州三村61号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[]},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919190679, 10010, 1583919190679, 0, '', 101079, '武汉市普仁医院', NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101082, NULL, NULL, 1001, 1014, '武汉市普仁广惠医院', NULL, '广惠医院是由武汉461厂职工医院转型而来的，隶属武汉普仁医院，中心成立于1958年，地址武汉市青山区武东街武东路100号，交通便利，人口密集，中心占地面积4000余平方米，建筑面积2000余平方米，是一所集医疗、急诊、急救、预防保健、康复、社区卫生服务为一体的非营利性综合医院，是武汉市基本医疗保险定点医院、武汉市基本医疗保险重症定点医院、武汉市生育保险定点医院。', NULL, NULL, NULL, NULL, NULL, NULL, '10100000', '10420000', '10420100', '武汉市青山区武东街武东路100号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[]},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919191409, 10010, 1583919191409, 0, '', 101079, '武汉市普仁医院', NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101083, NULL, NULL, 1001, 1014, '武汉普仁青船医院', NULL, '普仁青船医院隶属于湖北普仁集团，是扎根于青船地区周边社区，主动向所有受众提供健康服务（包括医疗、养老一体，慢病管理，体检，医疗特色专科等）的一级医疗机构。秉承技术领先、服务优质、群众满意、社会信赖的医院宗旨，以最大热忱和优质服务回报社会！', NULL, NULL, NULL, NULL, NULL, NULL, '10100000', '10420000', '10420100', '武汉市青山区工业港21幢', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[]},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919191426, 10010, 1583919191426, 0, '', 101079, '武汉市普仁医院', NULL, NULL, NULL, NULL, NULL);
