INSERT INTO `t_hospital` VALUES (101088, NULL, NULL, 1001, 1009, '武汉市第三医院首义院区', NULL, '武汉市第三医院暨武汉大学附属同仁医院，前身是美国基督教会圣公会在中国设立的医院之一，始建于1875年。发展至今已经成为集医疗、科研、教学、预防、保健为一体的大型综合性三级甲等医院，承担着省、市、区三级突发公共卫生事件的应急救护及医疗救治任务。', NULL, '027-68894799', NULL, NULL, 'http://www.whsyy.net/', 'whsd3yy', '10100000', '10420000', '10420100', '武汉市武昌区彭刘杨路241号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"安  靖\",\"position\":\"伦理委员\"}],\"key1\":\"027-68894990\",\"key3\":\"syylunli@163.com\",\"key5\":\"武汉市武昌区彭刘杨路241号\",\"key7\":\"http://www.whsyy.net/index.php/index-show-tid-1829.html\",\"key8\":\"工作日\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"陈永刚\",\"position\":\"机构办主任\"},{\"cellStyleMap\":{},\"name\":\"陈军\",\"position\":\"机构办副主任\"},{\"cellStyleMap\":{},\"mobilePhone\":\"18502766584\",\"name\":\"邹吉利\",\"position\":\"机构办秘书\"},{\"cellStyleMap\":{},\"name\":\"熊迪\",\"position\":\"质控员\"}],\"key46\":\"武汉市武昌区彭刘杨路241号\",\"key47\":\"027-88850381\",\"key49\":\"027-68894990\",\"key50\":\"whsyygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919192378, 10010, 1583919192378, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
