INSERT INTO `t_hospital` VALUES (101090, NULL, NULL, 1001, 1009, '宜昌市中心人民医院', NULL, '宜昌市中心人民医院创立于1949年，2003年纳入三峡大学教学医院系列,挂牌成立三峡大学第一临床医学院，2015年增挂三峡大学附属中心人民医院，是一所集医疗、预防、保健、教学、科研于一体的综合性三甲医院、爱婴医院、美国微笑联盟定点医院和国际紧急救援中心网络医院。2008年被省卫生厅授予“三级优秀医院”称号，2012年被国家卫生部等多个部委联合授予“全国综合医院中医药工作示范单位”称号。2012年5月被省卫生厅和宜昌市政府定为建设区域性医疗卫生中心依托医院。', NULL, '0717-6483495', '0717-6482302', 'yuanban@yc-hospital.com.cn', 'https://www.yczxyy.com/', NULL, '10100000', '10420000', '10420500', '湖北省宜昌市夷陵大道183号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0717-6486841\",\"key10\":\"1-2个月\",\"key17\":\"3000元/项\",\"key2\":\"0717-6483495\",\"key3\":\"ycllwyh@163.com\",\"key5\":\"宜昌市中心人民医院行政楼二楼\",\"key8\":\"工作日8：30-11:30 15:00-17:30\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"杨简\",\"telePhone\":\"0717-6487063\"}],\"key1\":\"http://gcp.yczxyy.com/\",\"key12\":\"3000元/项\",\"key46\":\"湖北省宜昌市夷陵大道183号\",\"key49\":\"0717-6487063\",\"key5\":\"Ⅱ-Ⅳ期药物临床试验、上市后再评价、医疗器械临床试验、 体外诊断试剂临床试验\",\"key9\":\"三级甲等\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919194077, 10010, 1583919194077, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101091, NULL, NULL, 1001, 1009, '鄂州市中心医院', NULL, '　鄂州市中心医院/武汉大学人民医院鄂州医院(原鄂州市第一医院)，始建于1946年，是鄂东南地区一所集医疗、教学、科研、康复、预防保健等功能为一体的综合性三级甲等医院，拥有国家级全科医学科住院医师规范化培训基地和13个省级住院医师培训基地。是湖北科技学院附属医院，武汉大学医学院、湖北中医药大学、湖北医药学院等多所高等院校的教学医院。', NULL, NULL, NULL, NULL, 'http://www.ezszxyy.com/', NULL, '10100000', '10420000', '10420700', '湖北省鄂州市鄂城区文星路9号', NULL, '436000', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"黄小林\",\"telePhone\":\"0711-3216298\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"赵辉\",\"telePhone\":\"0711-3258731\",\"title\":\"副主任委员\"}],\"key5\":\"湖北省鄂州市鄂城区文星路9号\",\"key7\":\"http://www.ezszxyy.com/ky/1492.jhtml\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[]},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919194921, 10010, 1583919194921, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101092, NULL, NULL, 1001, 1009, '武汉市第五医院', NULL, '医院创建于1923年爱尔兰传教士在汉阳显正街开设的诊所，后取名为圣·柯隆伴医院。1938年，伟大的国际主义战士白求恩曾在这所教会医院为抗战受伤的军民做过手术。栉风沐雨，春华秋实。经过90余年的建设与发展，武汉市第五医院如今已成为学科门类齐全、诊疗设备先进、就医环境优美、医疗技术精湛、医院管理科学的现代化综合医院。', NULL, NULL, NULL, NULL, NULL, NULL, '10100000', '10420000', '10420100', '湖北省武汉市汉阳区显正街122号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"1253653155@qq.com\",\"name\":\"程翠年\",\"telePhone\":\"027-84826184\"},{\"cellStyleMap\":{},\"email\":\"gxjkao@126.com\",\"mobilePhone\":\"18986202879\",\"name\":\"龚小军\"},{\"cellStyleMap\":{},\"email\":\"419913134@qq.com\",\"name\":\"丁明桥\",\"telePhone\":\"027-84812781\"}],\"key1\":\"027-84812429，18986202879\",\"key3\":\"whs5yymec@163.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"武汉市汉阳区显正街122号 武汉市第五医院住院四区三楼 \",\"key47\":\"027-84466620\",\"key49\":\"027-84466621\",\"key50\":\"WYYywlcsyjg@163.com \"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919195055, 10010, 1583919195055, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101093, NULL, NULL, 1001, 1009, '湖北省中西医结合医院', NULL, '座落在汉口菱角湖畔的湖北省中西医结合医院（湖北省新华医院）始建于1953年，是湖北省卫生计生委直属的国家三级甲等综合性医院、系湖北省职业病医院、湖北省脑科中心、湖北省医学体格检查中心、国家化学中毒救治（湖北）基地、湖北中医药大学附属医院、湖北中医药大学中西医结合临床学院，硕士联合培养基地，国家、湖北省住院医师规范化培训基地。', NULL, NULL, NULL, NULL, 'http://hbszxyjhyy.org.cn/', NULL, '10100000', '10420000', '10420100', '武汉市江汉区菱角湖路11号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"027-65658706\",\"key3\":\"hbzxyll@163.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"李思成\",\"telePhone\":\"027-85489871\"}],\"key46\":\"武汉市江汉区菱角湖路11号\",\"key49\":\"027-85489871\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919195064, 10010, 1583919195064, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
