INSERT INTO `t_hospital` VALUES (101097, NULL, NULL, 1005, 1009, '武汉市精神卫生中心二七院区', NULL, '武汉市精神卫生中心（武汉市心理医院）始建于1956年，1997年获批成为湖北省首家“三级甲等”精神专科医院，2018年成功复评。历经六十余年的建设发展，现已成为融医疗、教学、科研、预防、康复于一体的现代化医院。同时还是华中科技大学同济医学院附属武汉精神卫生中心暨第九临床学院、中国地质大学附属武汉心理医院、武汉市精神残疾康复技术指导中心等。先后获得“第一批国家特色医院文化医院”、“湖北省首批医务社工试点医院”、“全市百佳法治建设示范单位”、“市级文明单位”等称号。\n\n中心现有六角亭、二七两个院区，编制床位950张,年门急诊量30余万人次，出院患者数达万余人次。拥有3.0T医用磁共振成像系统等一批诊疗科研设备。连续举办具有国际影响力的中美、中法等精神分析培训项目，积极承办世界卫生组织（WHO）、国际精神分析协会（IPA）等国际组织的学术交流活动，获广泛关注，影响深远。', NULL, '82281733（二七院区）', NULL, NULL, 'http://www.chinapsy.com/', NULL, '10100000', '10420000', '10420100', '武汉市江岸区工农兵路89号', NULL, '430012', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"85398645@qq.com\",\"name\":\"徐艳芩\"}],\"key1\":\"027-59372344；027-59372347\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"fangmaosheng2006@163.com\",\"name\":\"房茂胜\",\"position\":\"机构办主任\"},{\"cellStyleMap\":{},\"email\":\"376642109@qq.com\",\"name\":\"徐晓津\",\"position\":\"机构办秘书\"}],\"key46\":\"武汉市硚口区游艺路93号\",\"key49\":\"027-82281635\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919196353, 10010, 1583919196353, 0, '', 100678, '武汉市精神卫生中心', NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101098, NULL, NULL, 1001, 1009, '湖北省中医院光谷院区', NULL, '    湖北省中医院暨湖北省中医药研究院、湖北中医药大学附属医院、国家中医临床研究基地，是全国唯一一所“四位一体”发展的省级三级甲等中医院，是国家中医药管理局首批全国示范中医院、国家临床药物试验机构、湖北省中医药数据中心。医院始建于1868年（清同治七年），前身是英国传教士杨格非博士创办的仁济医院，距今已有150年的历史。经过几代省中医人的辛苦耕耘，目前医院已发展成集医疗、教学、科研、保健、新药研发于一体的大型综合性省级中医院。2017年，医院被评为全国文明单位。', NULL, '027-87748001', NULL, NULL, 'http://hbhtcm.com/', NULL, '10100000', '10420000', '10420100', '武汉市洪山区珞瑜路856号（光谷广场前行100米）', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"465449749@qq.com ；chen1664@foxmail.com\",\"name\":\"陈学军\"},{\"cellStyleMap\":{},\"email\":\"zxx737@163.com\",\"name\":\"张馨\"},{\"cellStyleMap\":{},\"email\":\"4453577@qq.com；wjhbzyy@163.com\",\"name\":\"吴珺\"}],\"key1\":\"027-88920956；027-88929115\",\"key3\":\"ec@hbhtcm.com\",\"key8\":\"工作日\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"ljzwd@163.com\",\"name\":\"刘建忠\",\"position\":\"机构办主任\",\"telePhone\":\"027-88929224，027－87172190\"},{\"cellStyleMap\":{},\"email\":\"xinnuo3277@126.com\",\"name\":\"李羚青\",\"telePhone\":\"027-88927130，027-88920837\"}],\"key4\":\"工作日\",\"key46\":\"湖北省武汉市武昌区花园山4号 药物临床试验机构办公室\",\"key49\":\"027-88920837，027-88927130\",\"key50\":\"xinnuo3277@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919197098, 10010, 1583919197098, 0, '', 100237, '湖北省中医院', NULL, NULL, NULL, NULL, NULL);
