INSERT INTO `t_hospital` VALUES (101103, NULL, NULL, 1001, 1009, '武汉亚心总医院', NULL, '武汉亚心总医院位于湖北省武汉市经济技术开发区太子湖北路300号，是一家按三级医院标准建设的综合型医院  ，武汉市基本医疗保险定点医院。  医院重点打造心血管、骨科，胸外科，普外科，神经外科，泌尿外科，妇产科，儿科，重症医学科。是集医疗、教学、科研、预防、保健、急救（包括空中急救）和康复于一体的大型临床医学中心。', NULL, NULL, NULL, NULL, NULL, NULL, '10100000', '10420000', '10420100', '湖北省武汉市经济技术开发区太子湖北路300号', NULL, '430056', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"经济开发区太子湖北路300号\",\"key49\":\"027-84788999\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919199051, 10010, 1583919199051, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101104, NULL, NULL, 1001, 1009, '湖北省疾病预防控制中心', NULL, '湖北省预防医学科学院湖北省疾病预防控制中心（以下简称“院中心”）是经省编委批准，于2002年正式组建的省卫生厅直属的副厅级公益性事业单位，其前身为成立于1953年的中国医学科学院湖北分院和1958年的湖北省卫生防疫站。院中心位于武汉市洪山区卓刀泉北路，占地面积约19万平方米，内设6正处级行政管理处、室及2个副处级综合业务管理办公室（卫生应急办公室、质量技术管理办公室）和6个正处级业务所，即传染病防治研究所、血吸虫病防治研究所、慢性病防治研究所、卫生监测检验防护所、健康教育所、预防医学信息研究所。是全国专业设置最全、占地面积最大的省级疾病控制机构·', NULL, '省疾控中心值班电话：027-87652057  职业病咨询：027-87652012  健康体检咨询：027-87652023   科技查新检索电话：027-87740744', NULL, NULL, 'http://hbcdc.cn/', NULL, '10100000', '10420000', '10420100', '湖北省武汉市洪山区卓刀泉北路6号', NULL, '430079', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"13476011311\",\"name\":\"王兆\"}],\"key46\":\"湖北省武汉市洪山区卓刀泉北路35号\",\"key49\":\"13476011311\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919199134, 10010, 1583919199134, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
