INSERT INTO `t_hospital` VALUES (101106, NULL, NULL, 1007, 1009, '武汉血液中心', NULL, '武汉血液中心成立于1952年，是全国最早建立的采供血机构之一，历称武汉市中心血库、武汉市中心血站，2000年9月起使用现名至今，为全额预算拨款的公益性卫生事业单位。中心承担武汉市辖区内医疗机构的临床用血供应任务、本地区突发事件急救用血及向全省（邻省）提供稀有血型的任务，履行省血液中心的职能，并负责全省采供血机构的业务指导、专业技术人员的培训以及输血科学研究。', NULL, '捐血热线：027-83637079　 服务监督：027-83642022', NULL, NULL, 'https://www.whblood.org.cn/?AspxAutoDetectCookieSupport=1', NULL, '10100000', '10420000', '10420100', '武汉硚口区宝丰一路8号', NULL, '430030', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"杨茹\",\"telePhone\":\"027-83641790\"}],\"key46\":\"武汉硚口区宝丰一路8号\",\"key49\":\"027-83641790\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919200186, 10010, 1583919200186, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
