INSERT INTO `t_hospital` VALUES (101108, NULL, NULL, 1001, 1013, '武汉商职医院', NULL, '武汉商职医院始建于1952年，经过几代人的努力，现已发展成为一所集医疗、科研、教学、预防、保健和康复为一体的二级甲等综合医院，是华中科技大学同济医学院教学医院，武汉市首批医保定点医院，湖北省心脏房颤中心建设单位，湖北省心衰中心建设单位，湖北省医疗保险异地就医定点医疗机构，全国省级异地就医定点医院。\n\n \n\n医院占地5780平方米，建筑面积16000平方米，全院开放病床550张，医疗设备先进、齐全，技术力量雄厚。医院现有员工572人，其中高级职称医务人员64名，其中博士2人，硕士6人，硕士研究生导师2人，享受国务院特殊津贴专家1人。', NULL, '027-85666680 027-85661188\n\n', NULL, NULL, 'http://www.whszhosp.com/', NULL, '10100000', '10420000', '10420100', '武汉市江汉区大兴一路13号（龙王庙旁）', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"江汉路大兴一路13号\",\"key49\":\"15972550720\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919200289, 10010, 1583919200289, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
