INSERT INTO `t_hospital` VALUES (101112, NULL, NULL, 1001, 1009, '长沙市第一医院-南院', NULL, '岳麓东眺，湘水之滨，长沙市第一医院坐落在古城长沙市中心的营盘路，素以治院严谨， 医术精湛，名医荟萃，院容幽雅而名扬三湘。医院始建于1920年(民国9年)，原名为“湖南公医院”，是当时湖南省由国人自己创办的第一所西医医院，1949年10月25日由长沙市人民政府接管，更名为“长沙市立医院”，当时有病床100张。1958年，医院正式定名为“长沙市第一医院”，当时有病床300张。今年5月12日，医院已走过建院85周年的光辉历程。八十五年风风雨雨，八十五年的苦乐人生，八十五年的拼搏进取，使长沙市第一医院在湖南省会的医院史上留下厚重的一页。经过十几代一医人的艰苦奋斗，现已发展成为一所集医疗、教学、科研、预防、保健于一体的大型三级综合性医院。', NULL, '0731-82223156', NULL, 'admin@cssyyy.com', 'http://www.cssyyy.com', NULL, '10100000', '10430000', '10430100', '湖南省长沙市芙蓉水絮塘巷67号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"罗恒\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"何鸽飞\",\"telePhone\":\"0731-84667642\",\"title\":\"伦理会秘书\"}],\"key1\":\"\\t0731-84667642\",\"key17\":\"会议审查5000元\",\"key21\":\"3000元\",\"key5\":\"会议楼406会议室\",\"key8\":\"周一至周五下午\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"周继如\",\"title\":\"机构主任\"},{\"cellStyleMap\":{},\"email\":\"259412771@qq.com\",\"name\":\"张雪红\",\"title\":\"办公室主任\"},{\"cellStyleMap\":{},\"email\":\" 693794994@qq.com\",\"name\":\"张顺芝\",\"title\":\"办公室副主任\\t\"},{\"cellStyleMap\":{},\"email\":\"2514568340@qq.com\",\"mobilePhone\":\"15116319468\",\"name\":\"吴蔚\",\"title\":\"秘书\"},{\"cellStyleMap\":{},\"email\":\" 4920959@qq.com\",\"mobilePhone\":\"13975829501\",\"name\":\"陈恒\",\"title\":\"质控员\"},{\"cellStyleMap\":{},\"email\":\"476801218@qq.com\",\"mobilePhone\":\"13973137034\",\"name\":\"陈蓓\",\"title\":\"药物管理\"},{\"cellStyleMap\":{},\"email\":\" 254906951@qq.com\",\"mobilePhone\":\"15973158294\",\"name\":\"邓轩宇 \",\"title\":\"项目管理员\"}],\"key1\":\"http://www.cssdyyy.com/html/ky/chengguo/\",\"key4\":\"周一至周五8：30-12：00 14：30-17：00\",\"key46\":\"长沙市第一医院5号楼室三楼\",\"key47\":\"0731-84667643\",\"key49\":\"0731-84667643\",\"key50\":\"cssyjgb@qq.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleM