INSERT INTO `t_hospital` VALUES (101173, NULL, NULL, 1001, 1009, '赣南医学院第一附属医院黄金院区', NULL, '赣南医学院第一附属医院有两个院区，章贡院区位于江西省赣州市章贡区青年路23号，始建于1958年，占地面积208亩，是一所集医疗、教学、科研、预防于一体的三级甲等综合性医院，是赣州市医保和新农合定点医院。', NULL, NULL, NULL, NULL, 'http://www.gyyfy.com/', NULL, '10100000', '10360000', '10360700', '江西省赣州市经开区金岭路128号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"gnxxj6709@163.com\",\"name\":\"徐小军\",\"position\":\"伦理主任\"},{\"cellStyleMap\":{},\"email\":\"gyfyllwyh@163.com\",\"mobilePhone\":\"18870701616\",\"name\":\"方奕巍\",\"position\":\"伦理秘书\"}],\"key1\":\"0797-8689370\",\"key3\":\"gyfyllwyh@163.com\",\"key5\":\"赣州市青年路23号赣南医学院第一附属医院章贡院区1号住院楼1楼\",\"key8\":\"工作日\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"zgx8778@163.com\",\"name\":\"张国玺\",\"position\":\"机构主任\"},{\"cellStyleMap\":{},\"email\":\"gzyanjinling@163.com\",\"name\":\"严金玲\",\"position\":\"机构办主任\"},{\"cellStyleMap\":{},\"email\":\"zxiaojuan8810@126.com\",\"name\":\"钟小娟\",\"position\":\"机构秘书\"},{\"cellStyleMap\":{},\"email\":\"belinda0512@163.com\",\"name\":\"李蓓\",\"position\":\"质控员\"},{\"cellStyleMap\":{},\"email\":\"944849774@qq.com\",\"name\":\"李斌\",\"position\":\"药物管理员\"},{\"cellStyleMap\":{},\"email\":\"84558571@qq.com\",\"name\":\"李方方\",\"position\":\"资料管理员\"},{\"cellStyleMap\":{},\"email\":\"gaochang1989.ok@163.com\",\"name\":\"高畅\",\"position\":\"信息管理员\"}],\"key4\":\"工作日\",\"key46\":\"赣州市青年路23号赣南医学院第一附属医院章贡院区2号住院楼2楼\",\"key49\":\"0797-8689369\",\"key50\":\"gyyfygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919283249, 10010, 1583919283249, 0, '', 100127, '赣南医学院第一附属医院', NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101174, NULL, NULL, 1001, 1009, '新余市人民医院', NULL, '新余市人民医院始建于1932年1月，是新余市唯一的集医疗、教学、科研、预防、保健、康复、急救和社区卫生服务为一体的三级甲等综合医院。现有编制床位1400张，员工1806人，其中在编人员722人，聘用人员963人，临时工121人。现有员工中包括执业医师480人，护士902人。有职能管理科室16个，临床医技科室49个，其中临床一级科室32个，医技科室10个。', NULL, '0790-6651018', NULL, NULL, 'https://www.xyhos.com/', NULL, '10100000', '10360000', '10360500', '江西省新余市新欣北大道369号', NULL, '338000', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0790-6651021，18970991769\\n，0790-6651022\",\"key3\":\"xysrmyygcp@163.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"18870277665\",\"name\":\"何老师\"}],\"key46\":\"江西省新余市新欣北大道369号\",\"key49\":\"0790-6651049，18970991769\",\"key50\":\"xysrmyygcp@163.com，22689906@qq.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919283892, 10010, 1583919283892, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101175, NULL, NULL, 1005, 1009, '南昌大学附属口腔医院', NULL, '      南昌大学附属口腔医院实行南昌大学口腔医学院、南昌大学附属口腔医院、江西省口腔医院、江西省口腔病研究所四位一体的管理体制，是集医疗、教学、科研、预防、保健、培训为一体的三级甲等口腔专科医院。前身系创建于1980年的江西医学院口腔医学系，2003年增名“江西省口腔医院”，2005年由江西医学院附属口腔医院更名为南昌大学附属口腔医院。', NULL, NULL, NULL, NULL, 'http://www.jxskqyy.com/', NULL, '10100000', '10360000', '10360100', '中国江西南昌市福州路49号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"17707099176\",\"name\":\"江欢英\"}],\"key46\":\"江西省南昌市东湖区福州路49号\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919284606, 10010, 1583919284606, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
