INSERT INTO `t_hospital` VALUES (101177, NULL, NULL, 1003, 1009, '南昌市第二医院', NULL, '江西省中西医结合医院（江西中医药大学第四附属医院）始建1950年，位于南昌市区中心的八一大道起点、老福山畔，是集医疗、教学、科研、预防、保健、康复为一体的国家首批三级甲等中西医结合医院、全国首批重点中西医结合医院，现有职工1083人，其中卫生技术人员953人，高级职称专家159人，中级职称335人，医学博士、硕士140人，开放床位820张，设临床、医技科室50个，为省、市医保和创伤急救的定点医院。', NULL, NULL, NULL, NULL, 'http://542x650060.bcc.eiewz.cn/index.html', NULL, '10100000', '10360000', '10360100', '南昌市八一大道90号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key49\":\"0791-86297294\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919286355, 10010, 1583919286355, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
