INSERT INTO `t_hospital` VALUES (101185, NULL, NULL, 1001, 1009, '中国医科大学附属第二医院', NULL, '中国医科大学附属盛京医院是一所大型综合性现代化数字化大学附属医院。目前，医院共有三个院区和一个教育研发基地，总占地面积98.42万平方米，总建筑面积85.54万平方米。南湖、滑翔和沈北三个医疗院区均位于辽宁省沈阳市，总建筑面积为69.95万平方米。盛京医院医药研究教育发展基地位于辽宁省本溪市高新区“中国药都”，总建筑面积15.58万平方米。2014年“盛京医院 SHENGJING HOSPITAL”成功申请为“中国驰名商标”，盛京医院成为国内第一家拥有中国驰名商标的综合性医院。', NULL, '024-96615', NULL, NULL, 'http://www.sj-hospital.org/', NULL, '10100000', '10210000', '10210100', '沈阳市铁西区滑翔路39号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"赵玉虹\",\"title\":\"主任委员\"},{\"cellStyleMap\":{},\"name\":\"郭锡斌\",\"title\":\"副主任委员\"},{\"cellStyleMap\":{},\"email\":\"chaiyj@sj-hospital.org\",\"name\":\"柴英杰\",\"title\":\"秘书\"},{\"cellStyleMap\":{},\"email\":\"wangh3@sj-hospital.org\",\"name\":\"王洪\",\"title\":\"秘书\"}],\"key1\":\"024-96615-10027\",\"key2\":\"024-23883662\",\"key3\":\"wangh3@sj-hospital.org\",\"key5\":\"沈阳市铁西区滑翔路39号中国医科大学附属盛京医院（滑翔校区）8号楼4层党办\",\"key8\":\"周一，周三上午8:10-10:00；下午13:40-15:00\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"李慧\",\"telePhone\":\"024-96615-10169\",\"title\":\"机构主任\"}],\"key4\":\"周一至周四\",\"key46\":\"沈阳市铁西区滑翔路39号中国医科大学附属盛京医院 滑翔院区三号楼十楼药物临床试验机构\",\"key49\":\"024-96615-71146\",\"key50\":\"jgbgs@sj-hospital.org\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919300389, 10010, 1583919300389, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
