INSERT INTO `t_hospital` VALUES (101190, NULL, NULL, 1001, 1009, '宁夏医科大学附属医院', NULL, '八十余载风雨兼程，医院现已发展成为集医疗、教学、科研、考试、培训等职能于一体的综合性三级甲等医院，占地350余亩，建筑面积367536平方米。设心脑血管病医院、肿瘤医院、口腔医院3家直属分支机构，2012年成立宁夏医科大学总医院医院集团，现有29家会员单位。实际开放床位3684张，设有51个临床科室，12个医技科室。2018年1-6月总诊疗137.73万人次，出院6.42万人次，住院手术2.46万人次。\n\n医院现有职工5691人，卫生专业技术人员4760人，高级职称1187人，博士119人，硕士1184人，教师940人。“卫生部有突出贡献中青年专家”2人，自治区“塞上英才”5人。中国青年科技奖1人，宁夏青年科技奖4人，自治区“塞上名医”10人，自治区青年拔尖人才12人。享受国务院政府特殊津贴12人，自治区政府特殊津贴19人。入选国家“百千万人才工程”第三层次8人，自治区“313人才工程”20人。', NULL, '0951-6744457 ', NULL, NULL, 'http://www.nyfy.com.cn/', NULL, '10100000', '10640000', '10640100', '银川市兴庆区胜利街804号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"13895189856\",\"name\":\"高改莉\",\"title\":\"伦理委员会\"}],\"key1\":\"0951-6743648\",\"key2\":\"0951-6743648\",\"key3\":\"nyfymzjys@126.com\",\"key5\":\"总医院8号楼药库病案楼三楼\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"孙芳\"}],\"key46\":\"宁夏银川市胜利街804号 医院16号楼氧疗楼二楼\",\"key47\":\"0951-4071837\",\"key49\":\"0951-6744631\",\"key50\":\"nyfyyljdHT@163.com，nyfyyljd@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919320700, 10010, 1583919320700, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
