INSERT INTO `t_hospital` VALUES (101307, NULL, NULL, 1001, 1009, '新昌县中医院', NULL, ' 新昌中医院是一所集医疗、科研、教学、预防、保健、康复为一体的国家三级甲等中医医院，是全县中医药工作的龙头单位，是新昌县惠民医院，浙一医院协作医院、浙江中医药大学教学医院、浙江省中医院新昌分院。医院创建于1982年， 1992年评定为二级乙等中医医院，2005年顺利通过二级甲等中医院评审。2008年，医院搬迁新址，规模进一步扩大，一座花园式的现代化中医医院与美丽的新昌山城无不体现着和谐相融的自然意境，搬迁新址为医院发展插上了腾飞的翅膀。', NULL, NULL, NULL, NULL, 'http://www.xcxzyy.com/', NULL, '10100000', '10330000', '10330600', '新昌县七星街道十九峰路188号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0575-86265020\",\"key3\":\"xcxzyykjk@126.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"浙江省新昌县七星街道十九峰路188号\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919477412, 10010, 1583919477412, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101308, NULL, NULL, 1001, 1010, '瑞安市人民医院', NULL, '瑞安市人民医院（温州医科大学附属第三医院）是瑞安地区规模最大的集医疗、科研、教学、预防、保健、康复于一体的大型综合性医院，是浙江省卫生厅核准的综合性三级乙等医院。', NULL, NULL, NULL, NULL, 'http://www.rahos.gov.cn/', NULL, '10100000', '10330000', '10330381', '瑞安市瑞枫大道168号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0577-65866287\",\"key2\":\"0577-65866287\",\"key3\":\"ruiyilunli@126.com\",\"key5\":\"浙江省瑞安市玉海街道 万松路38号 一楼 临床研究伦理委员会办公室\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"罗文朝\",\"telePhone\":\"0577-65866017\"},{\"cellStyleMap\":{},\"name\":\"黄鹏\",\"telePhone\":\"0577-65866400\"},{\"cellStyleMap\":{},\"name\":\"沈秀微\",\"telePhone\":\"0577-65866275\"}],\"key46\":\"浙江省瑞安市万松路108号瑞安市人民医院11号楼5楼\",\"key49\":\"0577-65866275\\n，0577-65866239\\n，13587893357\",\"key50\":\"rygcp01@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919478088, 10010, 1583919478088, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101309, NULL, NULL, 1001, 1010, '衢州市第三医院', NULL, '衢州市第三医院创建于1989年，是以精神卫生、康复医疗为特色的三级乙等专科医院，2014年1月增设衢州市老年康复医院第二名称。现为衢州市精神卫生中心、心理咨询与治疗中心、老年医学中心、康复医学中心和衢州市社区戒毒治疗定点医院。2016年9月组建了衢州市第三医院医疗集团，医院承担医疗、康复、教学、科研、司法鉴定等工作，是衢州学院、衢州职业技术学院的教学医院。与上海华山医院、浙江省立同德医院、浙大邵逸夫医院、中南大学精神卫生研究所、武汉精神卫生中心等医疗机构建立长期的协作关系，并加入了湘雅二院跨区域专科医联体、浙江老年病专科联盟等十余个医联体。 ', NULL, NULL, NULL, NULL, 'http://www.qzsdsyy.com/prices/1/', NULL, '10100000', '10330000', '10330800', '浙江省衢州市柯城区白云街道白云北大道226号', NULL, '324003', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"mobilePhone\":\"18868070419\",\"name\":\"张驰\"}],\"key46\":\"衢州市柯城区白云北大道226号\",\"key50\":\"51128280@qq.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919479685, 10010, 1583919479685, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101310, NULL, NULL, 1001, 1009, '绍兴市人民医院', NULL, '　　绍兴市位于浙江省中北部、杭州湾南岸。东连宁波市，南临台州市和金华市，西接杭州市，北隔钱塘江与嘉兴市相望。地理位置优越，是极其理想的商贸物流中转集聚地。总面积为8256平方千米，人口480万。', NULL, '0575-88228888', NULL, NULL, 'http://www.312000.net/', NULL, '10100000', '10330000', '10330600', '市区中兴北路568号', NULL, '312000', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"Mxy4545@163.com\",\"name\":\"缪小燕\",\"telePhone\":\"0575-88229250\"}],\"key1\":\"0575-88229250\",\"key2\":\"0575-88229250\",\"key3\":\"mxy4545@163.com\",\"key5\":\"门诊四楼伦理办公室（图书馆旁）\",\"key8\":\"常规工作日\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"name\":\"丁洁卫\",\"position\":\"办公室主任\",\"telePhone\":\"0575-88228646\"},{\"cellStyleMap\":{},\"email\":\"lejaykon@163.com\",\"name\":\"康厉杰\",\"position\":\"机构秘书\",\"telePhone\":\"0575-88229270\"}],\"key4\":\"工作日8:00-12:00，14:00-17:00\",\"key46\":\"浙江省绍兴市中兴北路568号门诊四楼GCP办公室\",\"key47\":\"0575-88229270\",\"key49\":\"0575-88229270/0575-88228646\",\"key50\":\"lejaykon@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919479945, 10010, 1583919479945, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101311, NULL, NULL, 1003, 1009, '浙江省新华医院', NULL, '浙江中医药大学附属第二医院（浙江省新华医院）是一所集医疗、教学、科研和健康管理为一体的大型综合性三级甲等医院、全国三级甲等中西医结合医院、第三批全国重点中西医结合医院，也是浙江中医药大学直属的附属医院。', NULL, '(0571)85267000', NULL, NULL, 'http://www.xhhos.com/html/main/gb2312/index.html', NULL, '10100000', '10330000', '10330100', '浙江省杭州市潮王路318号', NULL, '310005', 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0571-85267016\",\"key3\":\"kjk0571@126.com\",\"key5\":\"浙江省杭州市潮王路318号\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"浙江省杭州市潮王路318号急诊楼六楼\",\"key47\":\"0571-88064725\",\"key49\":\"0571-85267123；0571-85267016\",\"key50\":\"zjsxhyygcp@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919480198, 10010, 1583919480198, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101312, NULL, NULL, 1001, 1009, '宁波市康宁医院', NULL, '医院始建于1919年，前身为民间募捐创立的镇海同义医院。1950年政府接管医院，民办转为公办，1952年更名为浙江省立宁波疗养院，1954年更名为宁波疗养院，1972年改制为宁波地区精神病医院，随后于1983年更名为宁波市精神病医院，1985年更名宁波市康宁医院至今。2012年通过三甲精神专科医院评审，2015年增挂宁波市精神疾病预防控制中心。历经百年变迁，医院已发展成为集医疗、科研、教学、预防于一体，综合实力较强的公立三级甲等精神专科医院，先后荣获中华精神科学会精神分裂症回归社会杰出贡献奖、中国百家百姓信赖的精神卫生医疗机构、全国精神卫生交流协作特别贡献奖、浙江省文明单位、浙江省平安医院、浙江省健康促进医院、2016-2018年度宁波市模范集体等荣誉。', NULL, NULL, NULL, NULL, 'http://www.nbknyy.com/', NULL, '10100000', '10330000', '10330200', '宁波市镇海区庄市街道庄俞南路1号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0574-26302533；0574--26302676\",\"key3\":\"nbknyyllwyh@126.com\",\"key5\":\"宁波市镇海区庄市街道庄俞南路1号\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"宁波市镇海区庄市街道庄俞南路1号行政楼一楼\",\"key49\":\"0574-26302520；0574-26302533\",\"key50\":\"knyygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919481303, 10010, 1583919481303, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101313, NULL, NULL, 1001, 1013, '湖州市第三人民医院', NULL, '湖州市第三人民医院创建于1951年11月，是浙江省三级甲等专科医院，原浙江省精神病院。医院以心理、精神障碍诊治（包括普通精神科、心身科、老年精神科、临床心理科、司法鉴定、儿少心理科、睡眠医学、心理应急救援等）为主，内科、老年病科、神经内科和风湿免疫科、外科、妇科等特色专科为辅，是一家以“大专科，兼综合”相互保障、共同发展为业务特色的医院。', NULL, NULL, NULL, NULL, 'http://www.hzsy.com/', NULL, '10100000', '10330000', '10330500', '湖州市苕溪东路2088号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0572-2162467\",\"key2\":\"0572-2162467\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key49\":\"0572-2023216\",\"key50\":\"ysl7250@126.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919481390, 10010, 1583919481390, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101314, NULL, NULL, 1001, 1009, '舟山医院', NULL, '舟山医院是一家集医疗、教学、科研、康复、保健于一体的三级甲等综合性医院。医院前身为1950年5月成立的定海县人民政府卫生院，1954年10月命名为浙江省立舟山医院，1987年1月，更名为舟山市人民医院，2009年12月经浙江省卫生厅批复同意更名为“舟山医院”。医院为省级文明医院和省级示范文明医院。2017年3月舟山医院顺利通过了国际医院评审联合委员会（JCI )的评审。', NULL, NULL, NULL, NULL, 'http://www.zs-hospital.com/', NULL, '10100000', '10330000', '10330900', '浙江省舟山市定海区千岛街道定沈路739号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[]},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key49\":\"13758008282\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919482629, 10010, 1583919482629, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101315, NULL, NULL, 1001, 1009, '浙江省人民医院', NULL, '   浙江省人民医院是集医疗、科研、教学、预防、保健、康复于一体的大型综合性三级甲等医院，隶属于浙江省卫生健康委员会，学科齐全、设备先进、技术雄厚，以“院有品牌、科有特色、人有专长”享誉省内外。承担着国家紧急医学救援队、国家核辐射救治基地、浙江省急救指挥中心等19个国家和省级机构任务。先后获得全国文明单位、全国卫生系统先进集体、全国平安医院、全国优质医疗服务示范医院、全国服务改善示范医院、全国管理创新医院和中国最佳医院管理团队奖等荣誉称号，近年来医院品牌影响力不断提升。', NULL, '87666666', NULL, NULL, 'http://www.hospitalstar.com/', NULL, '10100000', '10330000', '10330100', '杭州市下城区上塘路158号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0571-85893643\",\"key2\":\"0571-85893643\",\"key3\":\"zryllwyh@163.com\",\"key5\":\"浙江省杭州市上塘路158号 浙江省人民医院行政楼（7号楼）3楼 伦理委员会\",\"key8\":\"周一-周五 8:00-17:00\\n\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"lcywsy2012@163.com\",\"mobilePhone\":\"13738194591\\n\",\"name\":\"张望刚\",\"position\":\"机构秘书\"}],\"key4\":\"周一-周五 8:00-17:00\\n\",\"key46\":\"浙江省杭州市上塘路 浙江省人民医院行政楼（7号楼）2楼\\n\",\"key47\":\"0571-85893646\",\"key49\":\"0571-85893646\",\"key50\":\"zjsrmyygcp@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919483361, 10010, 1583919483361, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101316, NULL, NULL, 1001, 1009, '杭州市第六人民医院', NULL, '又名杭州市第六人民医院、浙江中医药大学附属杭州西溪医院,是一家以诊治肿瘤、肝病、感染性疾病为特色的集医疗、教学、科研、预防、保健为一体的市属三级甲等公立医院。', NULL, NULL, NULL, NULL, 'http://www.xixih.net/', NULL, '10100000', '10330000', '10330100', '杭州市西溪医院', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"（0571）86481703\",\"key2\":\"（0571）86481703\",\"key3\":\"tjhz2002@aliyun.com\",\"key5\":\"杭州市西湖区留下镇横埠街2号（310023）\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[{\"cellStyleMap\":{},\"email\":\"shenzhixian1967@126.com\",\"name\":\"沈芝仙\"}],\"key46\":\"浙江省杭州市文晖路201号\",\"key49\":\"0571-86481567；0571-86481822\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919485901, 10010, 1583919485901, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO `t_hospital` VALUES (101317, NULL, NULL, 1002, 1009, '浙江中医药大学附属第一医院', NULL, '浙江省中医院（浙江中医药大学附属第一医院）创建于1931年，位于浙江省杭州市上城区邮电路54号，是一所集医疗、科研、教学、保健、康复为一体的综合性三级中医医院。', NULL, NULL, NULL, NULL, 'http://www.zjhtcm.com/index.aspx', NULL, '10100000', '10330000', '10330100', '杭州市上城区邮电路54号', NULL, NULL, 0, 0, '{\"ethicsData\":{\"cellStyleMap\":{},\"contacts\":[],\"key1\":\"0571-87072953/87070579\",\"key3\":\"zjhtcmirb@163.com\"},\"geneticsOfficeData\":{\"cellStyleMap\":{},\"contacts\":[]},\"instructionData\":{\"cellStyleMap\":{},\"contacts\":[],\"key46\":\"浙江省杭州市江干区经济技术开发区9号大街9号(近物美下沙店)\",\"key49\":\"0571-87072519\",\"key50\":\"szyygcp@163.com；zjszyyywk@163.com\"},\"pharmacyData\":{\"cellStyleMap\":{}},\"smoAndCroPlan\":{\"cellStyleMap\":{},\"smoAndCroList\":[]}}', NULL, NULL, 10010, 1583919488655, 10010, 1583919488655, 0, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
